sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (((isLoading && !g_form.isNewRecord()) && (g_form.getValue('u_requested_for_email') != '' && g_form.getValue('u_requested_for') != '')) || newValue === '' | (newValue == oldValue && g_form.getValue('u_requested_for_email') != '')) {
        //alert("Early Return triggered");
		return;
    }

    var requestedForSysId = g_form.getValue('u_requested_for');
    //    alert("requestedForSysId" + requestedForSysId);

    var getEmailAddress = new GlideAjax('GetEmailAddress');
    getEmailAddress.addParam('sysparm_name', 'getEmail');
    getEmailAddress.addParam('sysparm_userID', requestedForSysId);

    getEmailAddress.getXML(populateEmailField);

    function populateEmailField(response) {
        // alert("response" + response);
        var requesterEmail = response.responseXML.documentElement.getAttribute("answer");
        // alert("requesterEmailResult" + requesterEmail);
        g_form.clearValue('u_requested_for_email');
        g_form.setValue('u_requested_for_email', requesterEmail);
    }


}
